import React, { useEffect ,useState} from "react";
import {useHistory } from "react-router-dom";
import { useSelector } from "react-redux";
import TextareaAutosize from "react-textarea-autosize";
import { Send } from "../../helper";
import Loader from "react-loader-spinner";


const Landing = () => {
  const isAuth = useSelector((state) => state.auth.isAuthenticated);
  const history = useHistory();
  const [diseases,setDiseases] = useState([]);
  const [loading,setLoading] = useState(false);
  const [symptoms,setSymptoms] = useState("");
  const [message,setMessage] = useState("");
  // If logged in redirect to dashboard
  useEffect(() => {
    if (isAuth) history.push("/dashboard");

  }, [isAuth]);

  const handleSubmit = async (e)=>{
    e.preventDefault();
    setLoading(true);
    let messages =["Loading...","Cool"];

    let res = await Send(symptoms);
    if(res.length  >0){
      setDiseases(res);
      setLoading(false);
    }
  }



  return (
    <div className="container">
      <form onSubmit={handleSubmit}>
        <div className="row justify-content-center align-items-center m-l70">
          <div className="col-8 p-0">
            <div className="inner__section mt-4">
              <div className="input-group main__input mb-3">
                <TextareaAutosize
                  type="text"
                  className="form-control input__type__inner"
                  placeholder="HOW DO YOU FEEL?"
                  aria-label="Username"
                  aria-describedby="basic-addon1"
                  minRows={1}
                  maxRows={6}
                  value={symptoms}
                  onChange={(e)=> setSymptoms(e.target.value)}
                />
              </div>
            </div>
          </div>
          <div className="col-1">
            <button className="btn btn-secondary btn__main text-uppercase" disabled={!symptoms}>Predict</button>
          </div>
        </div>
      </form>
      {loading === true &&  <div style={{ textAlign: "center" }}>
        <br></br>
        <br></br>
        <Loader
          type="ThreeDots"
          color="#6c757d"
          height={100}
          width={1000}
          visible={loading}
        />
        <span>{message}</span>
      </div>}
    </div>
  );
};

export default Landing;
